mapper.update:
update order_item
set order_id = #{orderId,jdbcType=BIGINT},
product_name = #{productName,jdbcType=VARCHAR},
product_price = #{productPrice,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, order_id, product_name, product_price
from order_item
where id = #{id,jdbcType=BIGINT}
